import { useState, useEffect } from 'react';
import VideoCard from '../components/VideoCard';

const LikedVideos = () => {
  const [likedVideos, setLikedVideos] = useState([]);
  const [loading, setLoading] = useState(true);

  // Sample videos data (same as in Home component)
  const allVideos = [
    {
      id: 1,
      title: "Introduction to Python Programming",
      channel: "TechEdu",
      views: "2.3M",
      time: "6 months ago",
      thumbnail: "https://picsum.photos/320/180?random=1",
      duration: "15:42"
    },
    {
      id: 2,
      title: "Latest iPhone 15 Review",
      channel: "TechReview",
      views: "1.8M",
      time: "3 days ago",
      thumbnail: "https://picsum.photos/320/180?random=2",
      duration: "12:35"
    },
    {
      id: 3,
      title: "Top 10 Gaming Moments of 2024",
      channel: "GameHighlights",
      views: "5.2M",
      time: "1 week ago",
      thumbnail: "https://picsum.photos/320/180?random=3",
      duration: "18:22"
    },
    {
      id: 4,
      title: "Amazing Pasta Recipe in 15 Minutes",
      channel: "QuickCook",
      views: "892K",
      time: "2 weeks ago",
      thumbnail: "https://picsum.photos/320/180?random=4",
      duration: "8:15"
    },
    {
      id: 5,
      title: "Travel Vlog: Tokyo Adventures",
      channel: "WanderlustDiary",
      views: "1.2M",
      time: "1 month ago",
      thumbnail: "https://picsum.photos/320/180?random=5",
      duration: "25:18"
    },
    {
      id: 6,
      title: "Best Music Hits of 2024",
      channel: "MusicMix",
      views: "3.7M",
      time: "2 months ago",
      thumbnail: "https://picsum.photos/320/180?random=6",
      duration: "45:30"
    },
    {
      id: 7,
      title: "Football Highlights - World Cup 2024",
      channel: "SportsCenter",
      views: "4.1M",
      time: "3 months ago",
      thumbnail: "https://picsum.photos/320/180?random=7",
      duration: "22:45"
    },
    {
      id: 8,
      title: "React.js Complete Tutorial",
      channel: "CodeAcademy",
      views: "1.5M",
      time: "4 months ago",
      thumbnail: "https://picsum.photos/320/180?random=8",
      duration: "2:15:30"
    }
  ];

  useEffect(() => {
    loadLikedVideos();
    
    // Listen for changes to liked videos
    const handleStorageChange = () => {
      loadLikedVideos();
    };

    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('likedVideosUpdated', handleStorageChange);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('likedVideosUpdated', handleStorageChange);
    };
  }, []);

  const loadLikedVideos = () => {
    setLoading(true);
    try {
      const likedVideoIds = JSON.parse(sessionStorage.getItem('likedVideos') || '[]');
      const likedVideosList = allVideos.filter(video => likedVideoIds.includes(video.id));
      setLikedVideos(likedVideosList);
    } catch (error) {
      console.error('Error loading liked videos:', error);
      setLikedVideos([]);
    } finally {
      setLoading(false);
    }
  };

  const handleVideoToggle = (videoId, isLiked) => {
    if (!isLiked) {
      // Remove from liked videos list
      setLikedVideos(prev => prev.filter(video => video.id !== videoId));
    }
    
    // Dispatch event to update other components
    window.dispatchEvent(new Event('likedVideosUpdated'));
  };

  const clearAllLiked = () => {
    if (window.confirm('Are you sure you want to remove all liked videos?')) {
      sessionStorage.removeItem('likedVideos');
      setLikedVideos([]);
      
      // Dispatch event to update other components
      window.dispatchEvent(new Event('likedVideosUpdated'));
    }
  };

  if (loading) {
    return <div className="loading">Loading liked videos...</div>;
  }

  return (
    <div className="liked-videos-page">
      <div className="page-header">
        <div className="header-content">
          <h1>Liked Videos</h1>
          <p>{likedVideos.length} {likedVideos.length === 1 ? 'video' : 'videos'}</p>
        </div>
        {likedVideos.length > 0 && (
          <button className="clear-all-btn" onClick={clearAllLiked}>
            <i className="fas fa-trash"></i>
            Clear All
          </button>
        )}
      </div>

      {likedVideos.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">
            <i className="fas fa-heart"></i>
          </div>
          <h2>No liked videos yet</h2>
          <p>Videos you like will appear here. Start exploring and like some videos!</p>
        </div>
      ) : (
        <div className="video-grid">
          {likedVideos.map(video => (
            <VideoCard 
              key={video.id} 
              video={video} 
              onWatchLaterToggle={handleVideoToggle}
              isLiked={true}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default LikedVideos;
